mapper.select:
select user_id, user_name, user_password, create_time, update_time
from demo.user_info
where isDeleted = 0;

select user_id, user_name, user_password, create_time, update_time
from demo.user_info
where user_name = #{user_name}
  and isDeleted = 0;

mapper.update:
update demo.user_info
set isDeleted   = 1,
    update_time = now()
where user_name = #{user_name}
  and user_password = #{user_password};
